import { createSlice } from "@reduxjs/toolkit";

export const todoSlice = createSlice({
    name: 'todo',
    initialState:{
        value:[
            {
                'id':1,
                'name':'Tarea 1',
                'description':'Realizar la tarea 1',
                'dueDate':'24-4-24'
            }
        ]
    },
    reducers:{
        addTodo: (state, action) => {
            state.value.push(action.payload)
            fetch("http://localhost:3001/tasks/addTask",{
                method:"POST",
                headers:{
                    "Content-type":"application/json",
                    "Authorization":"123456"
                },
                body: JSON.stringify(action.payload)
            }).catch(err=>{
                console.log(err)
            })
        },
        initAddTodo:(state,action) => {
            state.value.push(action.payload)
        },
        deleteTodo:(state,action) => {
            state.value = state.value.filter(todo => todo.id !== action.payload);
        },
        removeTodo: (state, action) => {
            state.value = state.value.filter(todo => todo.id !== action.payload);
            fetch("http://localhost:3001/tasks/removeTask/1715787771942.7563"+action.payload, {
                method: "DELETE",
                headers:{
                    "Content-Type":"application/json",
                    "Authorization":"123456"
                },
            }).catch(err=>{
                console.log(err);
            })
        }
    }
})

export const {addTodo, initAddTodo, deleteTodo, removeTodo} = todoSlice.actions;
export default todoSlice.reducer;
