import { createSlice } from "@reduxjs/toolkit";

export const goalsSlice = createSlice({
    name:'goals',
    initialState:{
        value: [
            {
                'id': 1,
                'name':'Correr',
                'description':'Salir a correr por la tarde',
                'dueDate':'24-4-24'
            },
        ]
    },
    reducers:{
        addTodo: (state, action) => {
            state.value.push(action.payload)
            fetch("http://localhost:3001/tasks/addTask",{
                method:"POST",
                headers:{
                    "Content-type":"application/json",
                    "Authorization":"123456"
                },
                body: JSON.stringify(action.payload)
            }).catch(err=>{
                console.log(err)
            })
        },
        initAddGoal:(state, action) => {
            state.value.push(action.payload);            
        },
        deleteGoal:(state, action) => {
            state.value = state.value.filter(goals => goals.id !== action.payload);
        }
    }
})

export const {initAddGoal, deleteGoal} = goalsSlice.actions;
export default goalsSlice.reducer;
